package com.werner.common.service;import com.werner.common.mapper.BaseMapper;import javax.annotation.Resource;public abstract class BaseService<T> {    public abstract BaseMapper<T> getMapper();    public int deleteByPrimaryKey(Integer primaryKey) {        return getMapper().deleteByPrimaryKey(primaryKey);    }    public int insert(T entity) {        return getMapper().insert(entity);    }    public int insertSelective(T entity) {        return getMapper().insertSelective(entity);    }    public T selectByPrimaryKey(Integer primaryKey) {        return getMapper().selectByPrimaryKey(primaryKey);    }    public int updateByPrimaryKeySelective(T entity) {        return getMapper().updateByPrimaryKeySelective(entity);    }}