package com.werner.controller;import com.alibaba.fastjson.JSON;import com.alibaba.fastjson.JSONObject;import com.werner.common.controller.BaseController;import com.werner.common.util.RequestData;import com.werner.common.util.ResponseData;import com.werner.entity.Banner;import com.werner.service.BannerService;import lombok.extern.slf4j.Slf4j;import org.springframework.web.bind.annotation.PostMapping;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import javax.annotation.Resource;import java.util.List;@Slf4j@RestController@RequestMapping("/needLogin/business/")public class BusinessController extends BaseController {    @Resource    private BannerService bannerService;    @PostMapping("getBannerList")    public ResponseData<List<Banner>> getBannerList() {            return null;    }    @PostMapping("getBannerById")    public ResponseData<Banner> getBannerById(RequestData requestData) {            JSONObject object = JSON.parseObject(requestData.getData().toString());            Banner banner = bannerService.selectByPrimaryKey(object.getInteger("id"));            return ResponseData.successful(banner);    }}