package com.werner.service;import com.werner.common.mapper.BaseMapper;import com.werner.common.service.BaseService;import com.werner.entity.User;import com.werner.mapper.UserMapper;import org.springframework.stereotype.Service;import javax.annotation.Resource;@Servicepublic class UserService extends BaseService<User> {    @Resource    private UserMapper userMapper;    @Override    public BaseMapper<User> getMapper() {        return userMapper;    }    public Boolean getUserByLogin(User user){        User userD=userMapper.selectUserByLogin(user);       return null==userD?false:true;    }}